import React from 'react';
import s from "./Dialogs.module.css"
import {NavLink} from "react-router-dom";
import Message from "./Message/Message";
import DialogItem from "./DialogItem/DialogItem";
import {sendMessageActionCreator, updateNewMessageBodyActionCreator} from "../../redux/state";




const Dialogs = (props)=>{
    // let usersData = [
    //     {id:1,name: "Andrey"},
    //     {id:2,name: "Alexandr"},
    //     {id:3,name: "Darya"},
    //     {id:4,name: "Ivan"},
    // ]
    //
    // let messageData = [
    //     {id:1,message: "Hello"},
    //     {id:2,message: "How are you, man?"},
    //     {id:3,message: "What interestind in your life?"},
    //     {id:4,message: "I'm glad to hear you!"},
    // ]
    let messagePage = props.store.getState().dialogsPage;
    let dialogs = messagePage.messageData.map(p=> <Message msg={p.message} source={p.num}/>)
    let users = messagePage.usersData.map(p=><DialogItem name={p.name} id={p.id} img={p.img}/>)
    let newMessageody = messagePage.messageData.newMessageBody;
    let newMessageElement = React.createRef();
    let addMessage =()=>{
        props.store.dispatch(sendMessageActionCreator());
    }
    let onNewMessageChange = (e)=>{
        let body = e.target.value;
        props.store.dispatch(updateNewMessageBodyActionCreator(body));
    }
    return(

            <div className={s.dialogs}>
                <div className={s.dialogsItems}>
                    {users}
                    {/*<DialogItem name={usersData[0].name} id={usersData[0].id}/>*/}
                    {/*<DialogItem name={usersData[1].name} id={usersData[1].id}/>*/}
                    {/*<DialogItem name={usersData[2].name} id={usersData[2].id}/>*/}
                    {/*<DialogItem name={usersData[3].name} id={usersData[3].id}/>*/}


                </div>
                <div className={s.dialogsMessage}>
                    {dialogs}
                    {/*<Message msg={messageData[0].message}/>*/}
                    {/*<Message msg={messageData[1].message}/>*/}
                    {/*<Message msg={messageData[2].message}/>*/}
                    {/*<Message msg={messageData[3].message}/>*/}
                    <div className={s.dialogsMessage_area}>
                        <div>
                            <textarea placeholder={"Enter Your message"} value={newMessageody} onChange={onNewMessageChange} ref={newMessageElement}/>
                        </div>
                        <div>
                            <button onClick={addMessage}>Send Message</button>
                        </div>
                    </div>
                </div>

            </div>


    )
}
export default Dialogs;
