import React from 'react';
import s from "./Users.module.css";
import * as axios from "axios";
import userPhoto from "../../assets/img/user.png"
import Users from "./Users";


class UserAPIComponent extends React.Component{

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response =>{
            this.props.setUsers(response.data.items)
        })
    }
    onPageChanged =(p) =>{
        this.props.setCurrentPage(p);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`).then(response =>{
            this.props.setUsers(response.data.items)
            debugger;
            this.props.setTotalUsersCount(response.data.totalCount)
        })

    }

    render(){

        return <Users totalUsersCount = {this.props.totalUsersCount} pageSize = {this.props.pageSize} currentPage = {this.props.currentPage}
                      onPageChanged ={this.onPageChanged} users = {this.props.users} follow = {this.props.follow} unfollow={this.props.unfollow}/>
    }
}

export default UserAPIComponent;
