import React from 'react';
import {addPostActionCreator, newPostTextActionCreator} from "../../redux/profile-reducer";
import Posts from "./Posts";
import StoreContext from "../../StoreContext";


const PostsContainer = (props)=>{
    let state = props.store.getState();

    let addPost =()=>{
        props.store.dispatch(addPostActionCreator());
    }
    let onPostChange = (text) =>{
        props.store.dispatch(newPostTextActionCreator(text));
    }

    return(
        <StoreContext.Consumer>
            <Posts addPost={addPost} updateNewPostText={onPostChange} postsData={state.profilePage.postsData}
                   newPostText={state.profilePage.newPostText}/>
        </StoreContext.Consumer>
    )
}
export default PostsContainer;
