import React from 'react';
import {connect} from "react-redux";
import {
    follow,
    setCurrentPage, setTotalUsersCount,
    setUsers, toggleIsFetching, unFollowText,
} from "../../redux/users-reducer";
import * as axios from "axios";
import Users from "./Users";
import circles from "./../../assets/img/circles.svg";
import Preloader from "../common/preloader/preloader";
import getUsers from "../../api/api";


class UserAPIComponent extends React.Component{

    componentDidMount() {//данный метод вызывается по умолчанию когда jsx уже прогрузился (комопнента react.component

        this.props.toggleIsFetching(true); //эта херня нужная чтобы включать и выключать анимацию

        getUsers().then(response =>{ //WidthCredentials - это значит использовать Cookie от сайта или нет!!
            this.props.toggleIsFetching(false);
            this.props.setUsers(response.data.items)
            this.props.setTotalUsersCount(response.data.totalCount)
        })
    }
    onPageChanged =(p) =>{
        this.props.setCurrentPage(p);
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`,{ withCredentials: true} ).then(response =>{
            this.props.setUsers(response.data.items)
            this.props.toggleIsFetching(false);
            // debugger;
            // this.props.setTotalUsersCount(response.data.totalCount)
        })

    }

    render(){

        return <>
            {this.props.isFetching? <Preloader/>: null}
            <Users totalUsersCount = {this.props.totalUsersCount} pageSize = {this.props.pageSize} currentPage = {this.props.currentPage}
                      onPageChanged ={this.onPageChanged} users = {this.props.users} follow = {this.props.follow} unfollow={this.props.unFollowText}
                 /></>
    }
}






let mapStateToProps = (state) => {
    return{
        users:state.usersPage.users,
        pageSize:state.usersPage.pageSize,
        totalUsersCount:state.usersPage.totalUsersCount,
        currentPage:state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,

    }
}

export default connect(mapStateToProps, {follow,unFollowText, setUsers, setCurrentPage, setTotalUsersCount, toggleIsFetching})( UserAPIComponent);
