const FOLLOW = "FOLLOW";
const UNFOLLOW ="UNFOLLOW";
const SETUSERS="SET-USERS";

let initialState = {
    users: [
        // {id:1,name: "Dmitriy", status:"Hello world", location:{city:"Minsk", country:"Belarus"}, followed:true},
        // {id:1,name: "Ivan", status:"I am a boss", location:{city:"Moscow", country:"Russia"}, followed:false},
        // {id:1,name: "Tanya", status:"ha ha ha", location:{city:"Kiev", country:"Ucraine"}, followed:true},
    ],

    newPostText:"it-camasutra.com"
};
const usersReducer =(state = initialState, action)=>{
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                    users: state.users.map(u => {
                        if (u.id === action.userId) {
                            return {...u, followed: true}
                        }
                        return u;
                    })
                }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SETUSERS:
            return {
                ...state,users: [ ...state.users ,...action.users, ]
            }

        default:{
            return state}
    }};
export const followActionCreator =(userID) =>{
    return {type:"FOLLOW",userID}
};
export const unFollowTextActionCreator = (userID) =>{
    return {type:'UNFOLLOW',userID}
};
export const setUsersActionCreator = (users) =>{
    return {type:SETUSERS,users}
};
export default usersReducer;
