import React from "react";

debugger;
class ProfileStatus  extends React.Component {

    state = {
        editMode:false,

    }
    activateEditMode = () =>{
        this.setState({ // асинхронный !
            editMode:true
        })
    }
    deactivateEditMode =() =>{
        this.setState({ // асинхронный !
            editMode:false
        })
        this.props.updateStatus();
    }
    render() {
        return (
            <div>

                {!this.state.editMode &&
                <div>
                    <span onDoubleClick={this.activateEditMode} >{this.props.status}</span>
                </div>
                }
                {this.state.editMode &&
                <div>
                    <input ref={ this.statusInputRef} onBlur={this.deactivateEditMode} autoFocus={true} type="text" value={this.props.status}/>
                </div>
                }
            </div>
        )
    }
}

export default ProfileStatus;
