const ADD_POST = "ADD-POST";
const UPDATE_NEW_POST_TEXT ="UPDATE-NEW-POST-TEXT";

let initialState = {
    postsData: [
        {id:1,text: "Hello", like:12},
        {id:2,text: "How are you, man?",like:11},
        {id:3,text: "What interestind in your life?",like:1},
        {id:4,text: "I'm glad to hear you!",like: 5}],
    newPostText:"it-camasutra.com"
};
const profileReducer =(state = initialState, action)=>{
    switch (action.type) {
        case ADD_POST:
            let newPost ={
                id:5,text:state.newPostText, like:0
            };

            state.postsData.push(newPost);
            state.newPostText = "";
            return state
        case UPDATE_NEW_POST_TEXT:
            state.newPostText = action.newText;
            return state
        default:
            return state
}};
export const addPostActionCreator =() =>{
    return {type:"ADD-POST"}
};
export const newPostTextActionCreator = (text) =>{
    return {type:'UPDATE-NEW-POST-TEXT', newText:text}
};
export default profileReducer;
