import React from 'react';
import s from "./Users.module.css";
import * as axios from "axios";
import userPhoto from "../../assets/img/user.png"


class User extends React.Component{


    constructor(props) {
        super(props);
        if(this.props.users.length === 0){
            axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response =>{
                this.props.setUsers(response.data.items)
            })
        }
    }

    componentDidMount() {
    }

    render(){
        let linkImg = "https://img.favpng.com/25/1/17/avatar-user-computer-icons-software-developer-png-favpng-7SbFpNeqKqhhTrrrnHFUqk6U4.jpg";
        return <div>
            {/*<button onClick={this.getUsers}> Get Users</button>*/}
            {
                this.props.users.map(u => <div className={s.users} key={u.id}>
                <span>
                    <div><img  className={s.users__img} src={u.photos.small != null? u.photos.small : userPhoto} alt=""/></div>
                    <div>
                        {u.followed ? <button onClick={()=>{this.props.unfollow(u.id)}}>UnFollow</button>:
                            <button onClick={()=>{this.props.follow(u.id)}}> Follow</button>
                        }
                    </div>
                </span>
                        <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.city"}</div>
                        <div>{"u.location.country"}</div>
                    </span>
                </span>

                    </div>
                )
            }
        </div>
    }
}

export default User;
