import React from 'react';
import Post from "./Post/Post";
import s from "./Posts.module.css"
const Posts = ()=>{
    let postsData = [
        {id:1,text: "Hello", like:12},
        {id:2,text: "How are you, man?",like:11},
        {id:3,text: "What interestind in your life?",like:1},
        {id:4,text: "I'm glad to hear you!",like: 5},
    ]


    let newPostElement = React.createRef();
    let addPost =()=>{
        let text = newPostElement.current.value;
        alert(text);
    }
    let posts = postsData.map(p =><Post text={p.text} like={p.like}/>)
    return(

        <div className={s.stuff}>
            <h2>My posts</h2>
            <div>
                <div>
                    <textarea ref={newPostElement}></textarea>
                </div>
                <div>
                    <button onClick={addPost}>Add Post</button>
                </div>
            </div>
            {posts}
            {/*<div>*/}
            {/*    <Post text={postsData[0].text} like={postsData[0].like}/>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <Post text={postsData[1].text} like={postsData[1].like}/>*/}
            {/*</div>*/}
            {/*<div>*/}
            {/*    <Post text={postsData[2].text} like={postsData[2].like}/>*/}
            {/*</div>*/}
        </div>
    )
}
export default Posts
