const FOLLOW = "FOLLOW";
const UNFOLLOW ="UNFOLLOW";
const SETUSERS="SET-USERS";
let link = "https://img.favpng.com/25/1/17/avatar-user-computer-icons-software-developer-png-favpng-7SbFpNeqKqhhTrrrnHFUqk6U4.jpg";
let initialState = {
    users: [
        {id:1,photoUrl:link, name: "Dmitriy", status:"Hello world", location:{city:"Minsk", country:"Belarus"}, followed:true},
        {id:1,photoUrl:link, name: "Ivan", status:"I am a boss", location:{city:"Moscow", country:"Russia"}, followed:false},
        {id:1,photoUrl:link, name: "Tanya", status:"ha ha ha", location:{city:"Kiev", country:"Ucraine"}, followed:true},
    ],

    newPostText:"it-camasutra.com"
};
const usersReducer =(state = initialState, action)=>{
    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                    users: state.users.map(u => {
                        if (u.id === action.userId) {
                            return {...u, followed: true}
                        }
                        return u;
                    })
                }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userId) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SETUSERS:
            return {
                ...state,users: [ ...state.users ,...action.users, ]
            }

        default:{
            return state}
    }};
export const followActionCreator =(userId) =>{
    return {type:"FOLLOW",userId}
};
export const unFollowTextActionCreator = (userId) =>{
    return {type:'UNFOLLOW',userId}
};
export const setUsersActionCreator = (users) =>{
    return {type:"SET-USERS",users}
};
export default usersReducer;
