import React from 'react';
import {connect} from "react-redux";
import {
    followActionCreator,
    setCurrentPageActionCreator, setTotalUsersCountActionCreator,
    setUsersActionCreator, toggleIsFetching,
    unFollowTextActionCreator
} from "../../redux/users-reducer";
import * as axios from "axios";
import Users from "./Users";
import circles from "./../../assets/img/circles.svg";




class UserAPIComponent extends React.Component{

    componentDidMount() {
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response =>{
            this.props.toggleIsFetching(true);
            this.props.setUsers(response.data.items)
            this.props.setTotalUsersCount(response.data.totalCount)
        })
    }
    onPageChanged =(p) =>{
        this.props.setCurrentPage(p);
        this.props.toggleIsFetching(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`).then(response =>{
            this.props.setUsers(response.data.items)
            this.props.toggleIsFetching(true);
            // debugger;
            // this.props.setTotalUsersCount(response.data.totalCount)
        })

    }

    render(){

        return <>
            {this.props.isFetching? <div style={{backgroundColor:"white"}}><img src={circles}/> </div>: null}
            <Users totalUsersCount = {this.props.totalUsersCount} pageSize = {this.props.pageSize} currentPage = {this.props.currentPage}
                      onPageChanged ={this.onPageChanged} users = {this.props.users} follow = {this.props.follow} unfollow={this.props.unfollow}
                 /></>
    }
}

let mapStateToProps = (state) => {
    return{
        users:state.usersPage.users,
        pageSize:state.usersPage.pageSize,
        totalUsersCount:state.usersPage.totalUsersCount,
        currentPage:state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching,

    }
}

let mapDispatchToProps = (dispatch) => {

    return{
        follow :(userId)=>{
            dispatch(followActionCreator(userId));
        },
        unfollow :(userId)=>{
            dispatch(unFollowTextActionCreator(userId));
        },
        setUsers :(users)=>{
            dispatch(setUsersActionCreator(users));
        },
        setCurrentPage: (pageNumber)=>{
            dispatch(setCurrentPageActionCreator(pageNumber))
        },
        setTotalUsersCount: (totalCount)=>{
            dispatch(setTotalUsersCountActionCreator(totalCount))
        },
        toggleIsFetching: (isFetching)=>{
            dispatch(toggleIsFetching(isFetching))
        }

    }
}

export default connect(mapStateToProps,mapDispatchToProps)( UserAPIComponent);
