import React from 'react';
import s from "./Users.module.css";
import userPhoto from "../../assets/img/user.png";
import NavLink from "react-router-dom/modules/NavLink";

let Users = (props) => {
    let linkImg = "https://img.favpng.com/25/1/17/avatar-user-computer-icons-software-developer-png-favpng-7SbFpNeqKqhhTrrrnHFUqk6U4.jpg";
    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    return <div>
        <div>
            {pages.map(p => {
                if (props.currentPage + 3 < p) {

                } else {
                    return (
                        <span className={props.currentPage === p && s.selected__page}
                              onClick={(e) => {
                                  props.onPageChanged(p)
                              }}> {p}</span>
                    )
                }

            })}
        </div>
        {
            props.users.map(u => <div className={s.users} key={u.id}>
                <span>
                    <div>
                        <NavLink to={"/profile/" +u.id}>
                             <img className={s.users__img} src={u.photos.small != null ? u.photos.small : userPhoto}
                                  alt=""/>
                        </NavLink>
                    </div>
                    <div>
                        {u.followed ? <button onClick={() => {
                                props.unfollow(u.id)
                            }}>UnFollow</button> :
                            <button onClick={() => {
                                props.follow(u.id)
                            }}> Follow</button>
                        }
                    </div>
                </span>
                    <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.city"}</div>
                        <div>{"u.location.country"}</div>
                    </span>
                </span>
                </div>
            )
        }
    </div>
}
export default Users;
