import React from 'react';
import {addPostActionCreator, newPostTextActionCreator} from "../../redux/profile-reducer";
import Posts from "./Posts";


const PostsContainer = (props)=>{
    let state = props.store.getState();
    let newPostElement = React.createRef();

    let addPost =()=>{
        props.store.dispatch(addPostActionCreator());
    }
    let onPostChange = () =>{
        let text = newPostElement.current.value;
        props.store.dispatch(newPostTextActionCreator());
    }

    return(<Posts addPost = {addPost} updateNewPostText ={onPostChange} postsData ={state.profilePage.postsData}
                  newPostText = {state.profilePage.newPostText}/>)
}
export default PostsContainer;
