import React from 'react';
import s from "./Users.module.css";
import userPhoto from "../../assets/img/user.png";

let Users = (props)=>{
    return <div>
        <div>
            {pages.map(p=>{
                if (this.props.currentPage +3 <p){

                }
                else {
                    return (
                        <span  className={this.props.currentPage === p && s.selected__page}
                               onClick={(e)=>{this.onPageChanged(p)}}> {p}</span>
                    )
                }

            })}
        </div>
        {
            this.props.users.map(u => <div className={s.users} key={u.id}>
                <span>
                    <div><img  className={s.users__img} src={u.photos.small != null? u.photos.small : userPhoto} alt=""/></div>
                    <div>
                        {u.followed ? <button onClick={()=>{this.props.unfollow(u.id)}}>UnFollow</button>:
                            <button onClick={()=>{this.props.follow(u.id)}}> Follow</button>
                        }
                    </div>
                </span>
                    <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.city"}</div>
                        <div>{"u.location.country"}</div>
                    </span>
                </span>
                </div>
            )
        }
    </div>
}
