import React, {useState} from 'react';
import s from "./Paginator.module.css";

let Paginator = ({currentPage, totalItemsCount, portionSize = 10}) => {

    let pagesCount = Math.ceil(props.totalItemsCount / props.pageSize);

    let portionCooutn = Math.ceil(pagesCount/ portionSize)

    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    return <div>
            {pages.map(p => {
                    return (
                        <span className={props.currentPage === p && s.selected__page}
                              onClick={(e) => {
                                  props.onPageChanged(p)
                              }}> {p}</span>
                    )
            })}
        </div>
}

export default Paginator;
