import React from 'react';
import s from "./Users.module.css";
import * as axios from "axios";
const Users = (props) => {



    if(props.users.length === 0){

        axios.get("https://social-network.samuraijs.com/api/1.0/users").then(response =>{
            props.setUsers(response.data.items)
        })
    }
    let linkImg = "https://img.favpng.com/25/1/17/avatar-user-computer-icons-software-developer-png-favpng-7SbFpNeqKqhhTrrrnHFUqk6U4.jpg";
    return <div>
        {
        props.users.map(u => <div className={s.users} key={u.id}>
                <span>
                    <div><img  className={s.users__img} src={u.photos.small != null? u.photos.small : linkImg} alt=""/></div>
                    <div>
                        {u.followed ? <button onClick={()=>{props.unfollow(u.id)}}>UnFollow</button>:
                            <button onClick={()=>{props.follow(u.id)}}> Follow</button>
                            }
                    </div>
                </span>
                <span>
                    <span>
                        <div>{u.name}</div>
                        <div>{u.status}</div>
                    </span>
                    <span>
                        <div>{"u.location.city"}</div>
                        <div>{"u.location.country"}</div>
                    </span>
                </span>

            </div>
        )
    }
    </div>

}

export default Users;
