import React from 'react';
import {addPostActionCreator, newPostTextActionCreator} from "../../redux/profile-reducer";
import Posts from "./Posts";

const PostsContiner = (props)=>{

    let newPostElement = React.createRef();

    let addPost =()=>{
        props.store.dispatch(addPostActionCreator());
    }
    let onPostChange = () =>{
        let text = newPostElement.current.value;
        props.store.dispatch(newPostTextActionCreator());
    }

    return(<Posts addPost = {addPost} updateNewPostText ={onPostChange} postsData ={props.postsData}/>)
}
export default PostsContiner;
